import React, { useState } from "react";
import { BettingOutcome, ArbitrageResult } from "../models/types";
import { ArbitrageCalculator } from "../utils/ArbitrageCalculator";
import { OddsConverter } from "../utils/OddsConverter";
import { v4 as uuidv4 } from "uuid";

const defaultOutcomes = [
  { id: uuidv4(), name: "Team A", odds: "+150" },
  { id: uuidv4(), name: "Team B", odds: "-180" },
];

function getDecimalOdds(odds: string) {
  return OddsConverter.americanToDecimal(odds);
}

export const MainContent: React.FC = () => {
  const [outcomes, setOutcomes] = useState<BettingOutcome[]>(
    defaultOutcomes.map((o) => ({
      ...o,
      decimalOdds: getDecimalOdds(o.odds),
    }))
  );
  const [bankroll, setBankroll] = useState("1000");
  const [kellyFraction, setKellyFraction] = useState(0.25);
  const [result, setResult] = useState<ArbitrageResult | null>(null);

  const handleOutcomeChange = (
    idx: number,
    field: "name" | "odds",
    value: string
  ) => {
    setOutcomes((prev) =>
      prev.map((o, i) =>
        i === idx
          ? {
              ...o,
              [field]: value,
              decimalOdds:
                field === "odds"
                  ? getDecimalOdds(value)
                  : o.decimalOdds,
            }
          : o
      )
    );
  };

  const addOutcome = () => {
    setOutcomes((prev) => [
      ...prev,
      { id: uuidv4(), name: `Outcome ${prev.length + 1}`, odds: "" },
    ]);
  };

  const removeOutcome = (idx: number) => {
    setOutcomes((prev) => prev.filter((_, i) => i !== idx));
  };

  const calculate = () => {
    const numBankroll = parseFloat(bankroll);
    if (isNaN(numBankroll) || numBankroll <= 0) return;
    setResult(
      ArbitrageCalculator.calculate(
        outcomes.map((o) => ({
          ...o,
          decimalOdds: getDecimalOdds(o.odds),
        })),
        numBankroll,
        kellyFraction
      )
    );
  };

  return (
    <div className="container">
      <h1>ArBETrage</h1>
      <p>Advanced Multi-Outcome Arbitrage Calculator</p>
      <div className="card">
        <h2>Manual Entry</h2>
        <label>
          Bankroll:
          <input
            type="number"
            value={bankroll}
            onChange={(e) => setBankroll(e.target.value)}
          />
        </label>
        <label>
          Kelly Fraction:
          <input
            type="range"
            min={0.1}
            max={0.5}
            step={0.05}
            value={kellyFraction}
            onChange={(e) => setKellyFraction(parseFloat(e.target.value))}
          />
          <span>{Math.round(kellyFraction * 100)}%</span>
        </label>
        <h3>Betting Outcomes</h3>
        {outcomes.map((o, idx) => (
          <div key={o.id} className="outcome-row">
            <input
              value={o.name}
              placeholder="Outcome name"
              onChange={(e) =>
                handleOutcomeChange(idx, "name", e.target.value)
              }
            />
            <input
              value={o.odds}
              placeholder="Odds (e.g. +150, -120)"
              onChange={(e) =>
                handleOutcomeChange(idx, "odds", e.target.value)
              }
            />
            {outcomes.length > 2 && (
              <button onClick={() => removeOutcome(idx)}>Remove</button>
            )}
          </div>
        ))}
        <button onClick={addOutcome}>Add Outcome</button>
        <button onClick={calculate}>Calculate Arbitrage</button>
      </div>
      {result && (
        <div className="card" style={{ marginTop: 24 }}>
          <h2>
            {result.hasArbitrage
              ? "✅ Arbitrage Found"
              : "⚠️ No Arbitrage"}
          </h2>
          <div>Margin: {result.margin.toFixed(2)}%</div>
          <div>Guaranteed Profit: ${result.guaranteedReturn.toFixed(2)}</div>
          <div>Total Stake: ${result.totalStake.toFixed(2)}</div>
          <div>Return Rate: {result.returnRate.toFixed(2)}%</div>
          <h3>Stakes:</h3>
          <ul>
            {outcomes.map((o) =>
              result.stakes[o.id] ? (
                <li key={o.id}>
                  {o.name}: ${result.stakes[o.id].toFixed(2)} @ {o.odds}
                </li>
              ) : null
            )}
          </ul>
          <h3>Scenarios:</h3>
          <ul>
            {outcomes.map((o) =>
              result.returns[o.id] !== undefined ? (
                <li key={o.id}>
                  If {o.name} wins: ${result.returns[o.id].toFixed(2)}
                </li>
              ) : null
            )}
          </ul>
        </div>
      )}
    </div>
  );
};
